import React from "react";
import img from "../asset/hero.png";
import { Container, Row, Col, Card, Button } from 'react-bootstrap';


const Home = () => {
  return (
    <>

    <section className="hero-section">
      <Container fluid className="p-0">
        <Row>
          <Col className="p-0 position-relative">
            <img
              src={img}  
              alt="Hero Image"
              className="img-fluid w-100"
            />

            {/* Card at the bottom left corner for desktop view */}
            <Card className="position-absolute bottom-0 start-0 m-3 d-none d-lg-block">
              <Card.Body>
                <Card.Title>Your Title</Card.Title>
                <Button variant="primary">Learn More</Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Card in a new row for mobile view */}
        <Row>
          <Col className="m-3 d-lg-none">
            <Card>
              <Card.Body>
                <Card.Title>Your Title</Card.Title>
                <Button variant="primary">Learn More</Button>
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>
    </section>
  </>
  );
};

export default Home;
